mapper.insert:

INSERT INTO
 PRO_BOARD
 (
    board_id
  , user_id
  , title
  , content
 )
VALUES
(
    board_id_seq.nextval
  , #{user_id}
  , #{title}
  , #{content}
)

mapper.update:

UPDATE
	 PRO_BOARD
SET 
     title = #{title} 
   , content = #{content}
   , modifyDate = SYSDATE	
WHERE
     board_id = #{board_id}
